mapper.insert:
INSERT INTO personal_board
	(
		boardnum
		, userid
		, title
		, contents

		, originalfile
		, savedfile

	)
VALUES
	(
		personal_board_seq.NEXTVAL
		, #{userid}
		, #{title}
		, #{contents}

		, #{originalfile}
		, #{savedfile}

	)

INSERT INTO personal_reply
	(
		replynum
		, boardnum
		, userid
		, replytext
	)
VALUES
	(
		personal_reply_seq.NEXTVAL
		, #{boardnum}
		, #{userid}
		, #{replytext}
	)
